class win_Options(QtWidgets.QMainWindow):
    signal_options = pyqtSignal(float, float, int, str, int, int, int, int, float)

    def __init__(self, parent=None):
        super(win_Options, self).__init__(parent)
        self.ui = Ui_Options()
        self.ui.setupUi(self)
        self.init_slots()

    def init_slots(self):
        self.ui.radioButton_1.clicked.connect(self.format)
        self.ui.radioButton_2.clicked.connect(self.format)
        self.ui.radioButton_3.clicked.connect(self.format)
        self.ui.pushButton_search.clicked.connect(self.search)
        self.ui.pushButton_save.clicked.connect(self.save)
        self.ui.pushButton_cancel.clicked.connect(self.Close)

    def MessageBox(self, title, dialog):
        msg_box = QMessageBox(self)  # 实例化一个QMessageBox对象
        msg_box.setWindowTitle(title)  # 设置对话框的标题
        msg_box.setText(dialog)  # 设置对话框的内容
        msg_box.setIcon(QMessageBox.Question)
        # 调用addButton方法传入字符串，确定按钮扮演的角色
        msg_box.addButton("是", QMessageBox.YesRole)
        msg_box.addButton("否", QMessageBox.NoRole)
        msg_box.exec()
        return msg_box.clickedButton().text()

    def format(self):
        if self.ui.radioButton_1.isChecked():
            self.ui.textBrowser.setText("1.炮二进五\n  馬8进7\n2.兵七进一\n　車9平8\n3.馬二进三\n　砲8平9\n4.馬八进七\n  卒7进1")
        elif self.ui.radioButton_2.isChecked():
            self.ui.textBrowser.setText("1.炮二进五  馬8进7\n2.兵七进一　車9平8\n3.馬二进三　砲8平9\n4.馬八进七  卒7进1")
        elif self.ui.radioButton_3.isChecked():
            self.ui.textBrowser.setText("1.炮二进五 馬8进7; 2.兵七进一 車9平8; 3.馬二进三 砲8平9; 4.馬八进七 卒7进1")

    def search(self):
        dir_choose = QFileDialog.getExistingDirectory(self, "请选择默认保存路径", self.path)
        self.ui.lineEdit_path.setText(dir_choose)

    def save(self):
        if self.ui.radioButton_1.isChecked():
            data3 = 1
        elif self.ui.radioButton_2.isChecked():
            data3 = 2
        else:
            data3 = 3
        data1, data2 = self.ui.lineEdit_skipGap.text(), self.ui.lineEdit_detectGap.text()
        data4, data5, data6 = self.ui.lineEdit_path.text(), self.ui.lineEdit_minR.text(), self.ui.lineEdit_maxR.text()
        data7, data8, data9 = self.ui.lineEdit_para1.text(), self.ui.lineEdit_para2.text(), self.ui.lineEdit_rate.text()
        if not data1.isalpha() and not data2.isalpha() and data5.isdigit() and data6.isdigit() and data7.isdigit() and \
                data8.isdigit() and not data9.isalpha():
            if float(data1) < 0 or float(data2) < 0:
                QtWidgets.QMessageBox.warning(self, u"中国象棋自动打谱工具", u"参数值必须为正！！")
                return
            if int(data5) <= 0 or int(data6) <= 0 or int(data7) <= 0 or int(data8) <= 0 or float(data9) <= 0:
                QtWidgets.QMessageBox.warning(self, u"中国象棋自动打谱工具", u"圆检测参数必须大于0！")
                return
            if int(data5) >= int(data6):
                QtWidgets.QMessageBox.warning(self, u"中国象棋自动打谱工具", u"圆检测的最小半径必须小于最大半径！")
                return
            if data4 != "" and not os.path.exists(data4):
                QtWidgets.QMessageBox.warning(self, u"中国象棋自动打谱工具", u"请输入合法的路径！")
                return
            if float(data9) > 1:
                if self.MessageBox("中国象棋自动打谱工具", "识别半径与棋子实际半径的比例大于1时会严重削弱程序的识别准确率，您确定要作此调整吗？") == "否":
                    return
            self.signal_options.emit(float(data1), float(data2), data3, data4, int(data5), int(data6), int(data7),
                                     int(data8), float(data9))
            self.close()
        else:
            QtWidgets.QMessageBox.warning(self, u"中国象棋自动打谱工具", u"参数必须为数字！")

    def Close(self):
        self.close()

